  if (!/^\d+([.]\d+)?$/g.test(state.amount)) {
    State.update({ wrongPrice: true })
  } else {
    try {
      State.update({ loading: true })
      Near.call(
        CONTRACT_ADDRESS,
        "add_paid_content",
        {
          link: `https://ipfs.near.social/ipfs/${state.img.cid}`,
          cost: parseNearAmount(state.amount),
          context_id: linkId
        }
      );
    } catch (err) {
      console.error(err);
