const Onboarding = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);

  animation: falling-animation 0.3s linear forwards;

  @keyframes falling-animation {
    from {
      transform: translate(-50%, -200%);
    }

    to {
      transform: translate(-50%, -50%);
    }
  }
`;

const handleClose = (isDoNotShowAgainChecked, viewedPages) => {
  if (data) {
    const time = Date.now()
    const mutation = data.find((ch) => ch?.id.includes('mutation'))?.id
    data.forEach((chapter) => {
      const isViewed = !!(viewedPages.includes(chapter.id) || lastShow[chapter.id].isViewed)
      const doNotShowAgain = !!((isDoNotShowAgainChecked && viewedPages.includes(chapter.id)) || lastShow[chapter.id].doNotShowAgain)
      Storage.privateSet(
        chapter.id + '/lastShow-test',
        {
          time,
          doNotShowAgain,
          mutation,
          isViewed,
        }
      )
    })
  }
  setShow(false)
}

const saveData = (inputData) => {
  if (context?.accountId) {
    Near.call(
      'app.webguide.near',
      'set_guide',
      {
        guide_id: props.link.id,
        data: inputData,
      }
    )
  }
}

return (
  <OverlayTriggerWrapper>
    {show ? (
      <DappletOverlay>
        <Onboarding>
          <Widget
            props={{
              handleClose,
              data,
              saveData,
              setShow,
              link: props.link,
              showFrom,
              oldRawData: response
            }}
            src="bos.dapplets.near/widget/OnboardingTest.SandboxOnboarding"
          />
        </Onboarding>
      </DappletOverlay>
    ) : null}
  </OverlayTriggerWrapper>
)

