  height: 42px;
  padding: 0px 20px;
  justify-content: center;
  align-items: center;
  border-radius: 90px;
  text-align: center;
  font-size: 14px;
  cursor: pointer;
  border: ${(props) => (props.$primary ? "initial" : "1px solid #E2E2E5")};
  background: ${(props) => (props.$primary ? "#02193A" : "initial")};
  color: ${(props) => (props.$primary ? "#fff" : "initial")};

  &:hover {
    background: ${(props) => (props.$primary ? "#1c3559" : "#eee")};
  }

  &:active {
    background: ${(props) => (props.$primary ? "#020c19" : "#ddd")};
  }
`

const {
  children,
  title,
  content,
  navi,
  onClose,
  // status, - conflict with deprecated Window.status property
  buttons,
  showChecked,
  checked,
  onDoNotShowChange,
} = props

const header = (
  <Header>
    <TopLine>
      <CalloutHeaderCaption>
        Step {navi?.currentChapterIndex + 1} of {navi?.totalChapters}
      </CalloutHeaderCaption>
      <PagesIndicators>
        {navi.totalPages > 1 && [...Array(navi.totalPages)].map(
          (_, index) => (
            <Navi
              key={index}
              $active={index == navi.currentPageIndex ? true : false}
            />
          )
        )}
      </PagesIndicators>
      <Close onClick={onClose}>{iconClose}</Close>
    </TopLine>
    <h1>{title}</h1>
  </Header>
)

const statuses = (
  <WrapperAlert $status={props.status.type}>
    <IconAlert>
      {props.status.type === "warning"
        ? warningIcon
        : props.status.type === "error"
        ? errorIcon
        : infoIcon}
    </IconAlert>
    <TextAlert>{props.status.text}</TextAlert>
  </WrapperAlert>
)

const checkbox = (
  <ContainerCheckbox>
    <CheckboxInput
      type="checkbox"
      checked={checked}
      onChange={onDoNotShowChange}
    />
    <Label htmlFor="checkbox">Don't show this guide again</Label>
  </ContainerCheckbox>
)

const navButtons = buttons?.length > 1 ? (
  <ActionsGroup>
    <ActionButton
      $primary={buttons[1].variant == "primary" ? true : false}
      onClick={buttons[1].onClick}
      disabled={buttons[1].disabled}
    >
      {buttons[1].label}
    </ActionButton>
    <ActionButton
      $primary={buttons[0].variant == "primary" ? true : false}
      onClick={buttons[0].onClick}
      disabled={buttons[0].disabled}
    >
      {buttons[0].label}
    </ActionButton>
  </ActionsGroup>
) : buttons?.length === 1 ? (
  <ActionsGroup>
    <ActionButton
      $primary={buttons[0].variant == "primary" ? true : false}
      onClick={buttons[0].onClick}
      disabled={buttons[0].disabled}
    >
      {buttons[0].label}
    </ActionButton>
  </ActionsGroup>
) : <></>

return (
  <InfoBox>
    <Container>
      {header}
      <Card>
        {props.status && props.status.text ? statuses : null}
        <Markdown text={content} />
      </Card>
      <Footer>
        {showChecked ? checkbox : null}
        {navButtons}
      </Footer>
    </Container>
  </InfoBox>
)

